/* --- Resting Heart Rate = RHR --- */
var randomNormal_RHR = require('random-normal');
var rand_RHR = randomNormal_RHR({mean:125, dev:15});
var rounded_RHR = Math.round(rand_RHR);

/* --- Cholestrol = C --- */
var randomNormal_C = require('random-normal');
var rand_C = randomNormal_C({mean:300, dev:100});
var rounded_C = Math.round(rand_C);

/* --- Fasting Blood Sugar = FBS --- */
var randomNormal_FBS = require('random-normal');
var rand_FBS = randomNormal_FBS({mean:105, dev:20});
var rounded_FBS = Math.round(rand_FBS);

/* --- Maximum Heart Rate = MHR --- */
var randomNormal_MHR = require('random-normal');
var rand_MHR = randomNormal_MHR({mean:150, dev:40});
var rounded_MHR = Math.round(rand_MHR);

/* --- Body Temperature = BT --- */
var randomNormal_BT = require('random-normal');
var rand_BT = randomNormal_BT({mean:37, dev:1});
var rounded_BT = rand_BT.toFixed(2);
/* var rounded_BT = Math.round(rand_BT);*/

/* --- Blood Pressure = BP --- */
var randomNormal_BP = require('random-normal');
var rand_BP = randomNormal_BP({mean:85, dev:20});
var rounded_BP = Math.round(rand_BP);


/* Create String with values */
var data = "resting heart rate, " + rounded_RHR + 
"; cholestrol, " + rounded_C + 
"; fasting blood sugar, " + rounded_FBS + 
"; maximum heart rate, " + rounded_MHR + 
"; body temperature, " + rounded_BT +
"; bloodpressure, " + rounded_BP;

msg.payload = data;
return msg;
